from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
links = Motor(Port.B, Direction.COUNTERCLOCKWISE)
rechts = Motor(Port.A, Direction.CLOCKWISE)
drive_base = DriveBase(links, rechts, 56, 130)


# The main program starts here.
# Geschwindigkeit einstellen
drive_base.settings(straight_speed=900)
# Korallenzucht
drive_base.straight(680)
drive_base.turn(-90)
drive_base.straight(140)
drive_base.straight(-50)
# Haifischbecken
drive_base.turn(50)
drive_base.straight(200)
drive_base.straight(-200)
# 3. Aufgabe
drive_base.turn(110)
drive_base.straight(400)
drive_base.turn(-113)
drive_base.straight(190)
# Anker
drive_base.straight(-200)
drive_base.turn(105)
drive_base.straight(105)
drive_base.turn(-55)
drive_base.straight(130)
drive_base.turn(-130)
drive_base.straight(500)
drive_base.turn(-30)
drive_base.straight(600)
