from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub_2 = PrimeHub()
motor_A = Motor(Port.C, Direction.CLOCKWISE)
motor_B = Motor(Port.D, Direction.COUNTERCLOCKWISE)
motor_c = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motor_A, motor_B, 56, 114)

# Initialize variables.
aktuelle_aufgabe = 1
los = False

def Matrix():
    prime_hub_2.display.off()
    prime_hub_2.display.number(aktuelle_aufgabe)

def Aufgabe_1():
    global aktuelle_aufgabe
    prime_hub_2.display.number(1)
    # Hier den Code für die Aufgabe 1 einfügen
    print('Aufgbe 1 wird gelöst.')
    motor_A.run_angle(100, 180)
    aktuelle_aufgabe = 2
    wait(1000)

def Aufgabe_2():
    global aktuelle_aufgabe
    prime_hub_2.display.number(2)
    # Hier den Code für die Aufgabe 2 einfügen
    motor_B.run_angle(20, 180)
    aktuelle_aufgabe = 3
    wait(10)

def Aufgabe_3():
    global aktuelle_aufgabe
    prime_hub_2.display.number(3)
    # Hier den Code für die Aufgabe 3 einfügen
    motor_c.run_angle(50, -30)
    wait(10)
    aktuelle_aufgabe = 4

def Aufgabe_4():
    # Hier den Code für die Aufgabe 4 einfügen
    drive_base.straight(250)
    wait(10)
    prime_hub_2.display.number(4)

def Start():
    global los
    los = True
    while los:
        if aktuelle_aufgabe == 1:
            Aufgabe_1()
        elif aktuelle_aufgabe == 2:
            Aufgabe_2()
        elif aktuelle_aufgabe == 3:
            Aufgabe_3()
        elif aktuelle_aufgabe == 4:
            Aufgabe_4()
        else:
            pass
        los = False
        prime_hub_2.system.set_stop_button(None)


# The main program starts here.
print('Start')
while True:
    prime_hub_2.system.set_stop_button(None)
    if Button.LEFT in prime_hub_2.buttons.pressed() and aktuelle_aufgabe > 1:
        print('links')
        aktuelle_aufgabe = aktuelle_aufgabe - 1
        wait(300)
        print(aktuelle_aufgabe)
    elif Button.RIGHT in prime_hub_2.buttons.pressed() and aktuelle_aufgabe < 5:
        print('rechts')
        aktuelle_aufgabe = aktuelle_aufgabe + 1
        wait(300)
        print(aktuelle_aufgabe)
    else:
        pass
    Matrix()
    if Button.CENTER in prime_hub_2.buttons.pressed():
        wait(1000)
        print(aktuelle_aufgabe)
        prime_hub_2.system.set_stop_button(Button.CENTER)
        Start()
    else:
        pass
